import React from 'react'
import {TileGrid, Props as GridProps} from 'components/tiles.d/tile-grid.component'
import {Entity, NullableEntityArray} from './country-search.component'

interface Props {
	results:NullableEntityArray
}


function randomDelay(min:number,max:number,resolveWith?:any){
	return new Promise(resolve=>{
		const delay=Math.random() * (max - min) + min;
		setTimeout(()=>{
			console.log('resolving with',resolveWith)
			resolve(resolveWith);
		},delay);
	})
}

export default class CountryResults extends React.Component<Props> {

	private tileContents:GridProps['tiles']={};


	componentDidUpdate(prevProps: Readonly<Props>): void {
		if(this.props.results){
			const tileContents:any={};
		    for(const ent of this.props.results){
				const tile:any={title:ent.value, className:'result-'+ent.type.toLowerCase()};
				switch(ent.type){
				case 'Country':
					tile.content=this.#generateCountryHtml(ent);
					break;
				case 'Continent':
					tile.content=this.#generateContinentHtml(ent);
					break;
				case 'Language':
					tile.content=this.#generateLanguageHtml(ent);
					break;
				}
				this.tileContents[ent.key]=tile
			}
		}
		console.log(this.props.results, this.tileContents)
	}

	#generateCountryHtml=(ent:Entity)=>{
		return randomDelay(1000,6000,(<span>{ent.value}</span>));
	}
	#generateContinentHtml=(ent:Entity)=>{
		return randomDelay(1000,6000,(<span>{ent.value}</span>));
	}
	#generateLanguageHtml=(ent:Entity)=>{
		return randomDelay(1000,6000,(<span>{ent.value}</span>));
	}

	render(){
		return (<div id="output">{
			this.props.results ?
				this.props.results.length ?
					<TileGrid tiles={this.tileContents}/>
					:<div className='emptyResults'>No results</div>
				:''
		}</div>)
	}





}
