import React from 'react'
import "./completion-input.css"; 


type CompletionItem={
	value:string
	,key?:string
	,html?:JSX.Element|JSX.Element[]
}
type CompletionList=CompletionItem[]
// type CompletionList=Array<{
// 	value:string
// 	,key?:string
// 	,html?:JSX.Element|JSX.Element[]
// }>

export type OnInputHandler<T extends CompletionList>=(search:string,lastCompletions:T|null)=>T|null

export type OnSubmitHandler=(...args:any[])=>void

export interface Props<T extends CompletionList> {
	id?:string,
	debounce?:number,
	onInput:OnInputHandler<T> ,
	onSubmit?:OnSubmitHandler
}

interface State<T extends CompletionList>{
	completions:T|null
	,suggestion:number
}

export class CompletionInput<T extends CompletionList> extends React.Component<Props<T>,State<T>> {


	public value='';


	constructor(props:Props<T>){
		super(props);
		this.state={completions:null,suggestion:-1};
	}

	get inputHandler(){
		if(this.props?.debounce && this.props.debounce>0)
			return CompletionInput.debounceHandler(this.#inputHandler,this.props.debounce,this);
		else
			return this.#inputHandler.bind(this);
	}
	#inputHandler(event:React.ChangeEvent<HTMLInputElement>){
		// console.log("updating this.state.completions",this)
		const last=this.value;
		const value=this.value=event.target.value;
		if(!value){
			this.#setCompletionState(null);
		}else if(this.props.onInput){
			let lastCompletions=null;
			if(value.startsWith(last)){
				lastCompletions=this.state.completions;
			}else{
				this.#setCompletionState(null); //user has pressed backspace => delete the old completions
			}
			this.#setCompletionState(this.props.onInput(value,lastCompletions));
		}
	}

	#setCompletionState(completions:State<T>["completions"]){
		this.setState({completions});
	}

	/* eslint-disable-next-line @typescript-eslint/ban-types -- we explicitly expect any function */
	static debounceHandler<F extends (...args:any[])=>any>(fn:F,delay=50,self:any=window) {
		var timer:ReturnType<typeof setInterval>; /* eslint-disable-line no-var -- explicitly refers to entire function */
		return (...args:Parameters<F>)=>{
			// console.log('debounce',...args);
			clearTimeout(timer);
			timer=setTimeout(()=>{
				// console.log('trigger',...args);
				try{fn.apply(self,args)}catch(e){console.error(e)}
			},delay);
		}
	}

	get keyboardHandler(){
		return this.#keyboardHandler.bind(this)
	}
	#keyboardHandler(event:React.KeyboardEvent<HTMLInputElement>){
		if(!event.target.hasOwnProperty('value')){
			console.warn("Event target didn't have 'value' prop:",event)
			return;
		}
		const input=event.target as unknown as HTMLInputElement
		// console.log(event,input);
		switch(event.key){
			case 'ArrowUp':{
				const suggestion=this.state.suggestion-1;
				if(suggestion>-1)
					this.setState({suggestion})
				break;
			}
			case 'ArrowDown':{
				const suggestion=this.state.suggestion+1;
				if(suggestion<=(this.state.completions?.length||0))
					this.setState({suggestion})
				break;
			}
			case 'Tab':{
				this.#applySuggestion(input);
				event.preventDefault();
				break;
			}
			case 'Enter':{
				this.#applySuggestion(input);
				if(this.props.onSubmit)
					this.props.onSubmit(input.value)
				else
					input.dispatchEvent(new Event("submit")) //alternative method
				break;
			}
			default:
			return;
		}
		setTimeout(()=>console.log(event.key,this.state,this),100);
	}

	get currentSuggestion(){
		if(this.state.completions && this.state.completions.length)
			return this.state.completions[Math.max(this.state.suggestion,0)]
		return undefined
	}
	#applySuggestion(input:HTMLInputElement){
		const suggestion=this.currentSuggestion
		if(suggestion){
			input.value=suggestion.value
			this.#inputHandler({target:input} as React.ChangeEvent<HTMLInputElement>)
		}
		return input.value;
	}



	render(){	
		// console.log("rendering...")	

		return (
			<div id={this.props.id} className="completionInput">
				<input 
					type="text" 
					placeholder="Search..." 
					onInput={this.inputHandler} 
					onKeyDown={this.keyboardHandler}
					autoFocus
				></input>
				{(this.state.completions && (this.state.completions.length>1 || this.value!=this.state.completions[0]?.value) )
					? <div className={`completionList`}>{
						this.state.completions.map(
							(c,i)=>(
								<div 
									key={c.key||c.value} 
									className={`completion ${i==this.state.suggestion?'active':''}`}
								>{c.html||c.value}</div>
							)
						)
					}</div>
					: ''
				}
			</div>
		);
	}


}


export default CompletionInput;
