import React from 'react'
import {CompletionInput,OnSubmitHandler} from 'components/completion-input.d/completion-input.component'
import "./country-input.css";
import {graphql,EntityType,toEntityType,Scalars} from 'services/countries-api/countries-api.service';

export interface Entity{
	value:Scalars['String']
	,code:Scalars['ID']
	,type:EntityType
	,key:Scalars['String']
	,html?:JSX.Element|JSX.Element[]
}

type NullableEntityArray=Entity[]|null;

const settings={
	debounce:300
	,inputId:"country-search"
}

export interface Props {
	onSubmit:OnSubmitHandler
}

export default class CountryInput extends React.Component<Props> {

	private entities:NullableEntityArray=null;

	constructor(props:never){
		super(props);

		graphql.getNamesAndCodes({})
			.then(data=>{
				const list:Entity[]=[];
				Object.entries(data).forEach(([t,arr])=>{
					if(Array.isArray(arr)){
						const type=toEntityType(t)
						arr.forEach(({code,name})=>{
							code=code.toUpperCase()
							const value=String(name||code);
							const key:Entity["key"]=`${code}-${type}`.toLowerCase();
							list.push({key, value, code, type});
						})
					}
				})
				if(list.length)
					this.entities=list
			})
	}

	getCompletions(search:string,entities:NullableEntityArray):NullableEntityArray {
		if(!entities)
			entities=this.entities; //if no subset of entities are passed in then use the entire list
		else if(!entities.length){
			return [];
		}
		// console.log("Check for completions to '"+search+"' in:",entities)

		if(!entities || !entities.length)
			return null;

		const regex=new RegExp((search.length<3 ? '(^)(' : '(.*)(')+search+')(.*)','i'); //short strings we only match begining
		if(search.length<3)
			return entities.filter(ent=>this.#setJSXOnMatch(ent,'value',regex)||this.#setJSXOnMatch(ent,'code',regex));
		else
			return entities.filter(ent=>this.#setJSXOnMatch(ent,'value',regex));
		//this^ can return an emtpy array, meaning no matches, meaning there is no point in typing more...
	}

	#setJSXOnMatch=(ent:Entity,prop:"value"|"code",regex:RegExp):boolean=>{
		const match=ent[prop].match(regex);
		if(match){
			const htmlArr:JSX.Element[]=[];
			match[1] && htmlArr.push((<span>{match[1]}</span>))
			htmlArr.push((<span className="highlight">{match[2]}</span>))
			match[3] && htmlArr.push((<span>{match[3]}</span>))
			htmlArr.push((<span className="type">{ent.type}</span>))
			if(prop=='code')
				htmlArr.push(<span> - {ent.value}</span>)
			ent.html=htmlArr
			return true;
		}
		return false;
	}


	render(){
		return (
			<CompletionInput 
				id={settings.inputId} 
				debounce={settings.debounce} 
				onInput={this.getCompletions.bind(this)} 
				onSubmit={this.props.onSubmit}
			/>
		)
	}
}
