import React from 'react'
import {Tile, Props as TileProps} from './tile.component'


type AlsoAsync<T>=T|Promise<T>
type Content=TileProps["content"];
export interface Props {
	tiles:Record<string,Omit<TileProps,'content'> & {content:Content|Promise<Content>}>
	whileLoading?:Content
}


export class TileGrid extends React.Component<Props> {

  	render() {return (
		<div className="tileGrid">{
			Object.entries(this.props.tiles).map(([key,{title,content,className}])=>
				<Tile key={key} title={title} className={className} content={content} />
			)
		}</div>  
  	)}

}

export default TileGrid
