import React from 'react'
import {Tile, Props as TileProps,Renderable} from './tile.component'

export type Props={
	tiles:Record<string,Omit<TileProps,'whileLoading'>>
	whileLoading?:TileProps["whileLoading"]
	onEmptyResults?:Renderable
}

const defaultProps={
	whileLoading:(<span className='loading'></span>)
	,onEmptyResults:(<div className='emptyResults'>No results</div>)
}

// export class TileGrid<T extends Props> extends React.Component<T,State<T>> {
export class TileGrid extends React.Component<Props> {

	private _ID:number;
	log(...args:any[]){
		console.log(this._ID,`<${this.constructor.name}>`,...args);
	}

	constructor(props:Props){
		const _props:Required<Props>=Object.assign({},defaultProps,props);
		super(_props);
		this._ID=Math.floor(Math.random()*10000);
		this.log("creating:",Object.values(this.props.tiles).map(t=>(`${t.title}${t.content instanceof Promise ?'*':''}`)).join(', '));
	}


  	render() {
  		this.log("rendering:",Object.values(this.props.tiles).map(t=>(`${t.title}${t.content instanceof Promise ?'*':''}`)).join(', '));
  		return (
			<div className="tileGrid">{
				Object.entries(this.props.tiles).map(([key,tileProps])=>
					<Tile key={key} {...tileProps}/>
				) 
				|| this.props.onEmptyResults
			}</div>  
  		)
  	}

}



export default TileGrid
