import React from 'react'
import "./tiles.css";

export interface Props {
	title:string
	content:JSX.Element|JSX.Element[]
	,className?:string
}
export interface State {
	marked:boolean
}

export class Tile extends React.Component<Props,State>{

	constructor(props:Props){
		super(props);
		this.state={
			marked:false
		}
	}
	toggleMarked(){
		this.setState({marked:!this.state.marked})
	}
	render(){
		const cls=['tile'];
		if(this.state.marked) cls.push('marked');
		if(this.props.className) cls.push(this.props.className)
		return (
			<div 
				className={cls.join(' ')}
				onClick={this.toggleMarked.bind(this)}
			>
				<div className="title">{this.props.title}</div>
				<div className="content">
					{this.props.content}
				</div>
			</div>
		);
	}
}

export default Tile
