import {
  graphql,
  EntityType,
  assertEntityType,
  Scalars,
  NamesAndCodesQuery,
  NamesAndCodesQueryVariables,
  NamesAndCodesDocument
} from 'services/countries-api/countries-api.service';


interface Entity{
  name:Scalars['String']
  ,code:Scalars['ID']
  ,type:EntityType
}

function makeEntityArray(data:NamesAndCodesQuery){
  const list:Array<Entity>=[];
  Object.entries(data).forEach(([type,arr])=>{
    if(Array.isArray(arr)){
      arr.forEach(({code,name})=>{
        list.push({code, name:String(name), type:assertEntityType(type)});
      })
    }
  })
  return list
}

export function getNamesAndCodes(){
  return graphql.request<NamesAndCodesQuery,NamesAndCodesQueryVariables>(NamesAndCodesDocument).then(makeEntityArray)
}

export default getNamesAndCodes;
