import React from 'react'
import {Tile, Props as TileProps,Renderable} from './tile.component'
type NullableTiles = Record<string,Omit<TileProps,'whileLoading'>>|null
export type Props={
	tiles: NullableTiles//null => don't show onEmptyResults
	whileLoading?:TileProps["whileLoading"]
	onEmptyResults?:Renderable
}

const defaultProps={
	whileLoading:(<span className='loading'></span>)
	,onEmptyResults:(<div className='emptyResults'>No results</div>)
}

// export class TileGrid<T extends Props> extends React.Component<T,State<T>> {
export class TileGrid extends React.Component {

	private _ID:number;
	log(...args:any[]){
		console.log(this._ID,`<${this.constructor.name}>`,...args);
	}

	private _props:Required<Props>

	constructor(props:Props){
		const _props:Required<Props>=Object.assign({},defaultProps,props);
		super(_props);
		this._props=_props;
		this._ID=Math.floor(Math.random()*10000);
		this.log("creating:",Object.values(_props.tiles||{}).map(t=>(`${t.title}${t.content instanceof Promise ?'*':''}`)).join(', '));
	}


  	render() {
  		if(this._props.tiles){
  			var showTiles=Object.entries(this._props.tiles);
  			var showEmpty=(showTiles.length ? '' : this._props.onEmptyResults)
  		}else{
  			showEmpty='';
  			showTiles=[];
  		}

  		this.log("rendering:",showTiles.map(([k,t])=>(`${t.title}${t.content instanceof Promise ?'*':''}`)).join(', '));
  		return (
			<div className="tileGrid">
				{showTiles.map(([key,tileProps])=>
					<Tile 
						key={key} 
						whileLoading={this._props.whileLoading}
						{...tileProps} 
					/>
				)}
				{showEmpty}
			</div>  
  		)
  	}

}


export default TileGrid
