import React from 'react'

type CompletionList=Array<{name:string}>

export declare interface getCompletionSignature {
	(search:string,lastResult?:CompletionList):CompletionList|null
}
export declare interface InputPropsInterface {
	id?:string,
	debounce?:number,
	getCompletions?:getCompletionSignature
}


export class Input extends React.Component<InputPropsInterface> {


	public lastSearch:Parameters<getCompletionSignature>[0]='';
	public completions:CompletionList|null=null;


	get inputHandler(){
		if(this.props?.debounce && this.props.debounce>0)
			return Input.debounce(this.#inputHandler,this.props.debounce,this);
		else
			return this.#inputHandler.bind(this);
	}

	matches=[];
	#inputHandler(event:React.ChangeEvent<HTMLInputElement>){
		const search=event.target.value;
		const lastSearch=this.lastSearch;
		this.lastSearch=search;
		if(!search){
			this.completions=null;
		}else if(this.props.getCompletions){
			var lastCompletions;
			if(lastSearch.startsWith(search)){
				lastCompletions=this.completions||undefined;
			}else{
				this.completions=null; //we've been erasing, delete the old completions
			}
		 	this.completions=this.props.getCompletions(search,lastCompletions);
		}
	}


	static debounce(fn:Function,delay:number,self:any=window){
		var timer:ReturnType<typeof setInterval>;
		return (...args:any)=>{
			clearTimeout(timer);
			timer=setTimeout(()=>fn.apply(self,args),delay);
		}
	}

	render(){
		var completionList
		if(this.completions){
			var list=this.completions.map(({name})=>(<div className='completion'>{name}</div>))
			completionList=(<div className="completionList">{list}</div>)
		}
		return (
			<div id={this.props.id}>
				<input type="text" placeholder="Search..." onInput={this.inputHandler} autoFocus></input>
				{completionList}
			</div>
		);
	}


}


export default Input;
