import React from 'react'
import {CompletionInput} from 'components/completion-input.d/completion-input.component'
import {getNamesAndCodes,Entity} from './names-and-codes.query'
import "./country-input.css";

type NullableEntityArray=Entity[]|null;

const settings={
	debounce:300
	,inputId:"country-search"
}

export default class CountryInput extends React.Component {

	private entities:NullableEntityArray=null;

	constructor(props:never){
		super(props)
		getNamesAndCodes().then(ent=>{if(ent.length){this.entities=ent}});
	}

	getCompletions(search:string,entities:NullableEntityArray):Entity[]|null {
		if(!entities || !entities.length)
			entities=this.entities; //if no subset of entities are passed in then use the entire list

		if(!entities || !entities.length)
			return null;

		const regex=new RegExp((search.length<3 ? '(^)(' : '(.*)(')+search+')(.*)','i'); //short strings we only match begining
		if(search.length<3)
			entities=entities.filter(ent=>this.#setJSXOnMatch(ent,'value',regex)||this.#setJSXOnMatch(ent,'code',regex));
		else
			entities=entities.filter(ent=>this.#setJSXOnMatch(ent,'value',regex));

		return (entities.length ? entities : null);
	}

	#setJSXOnMatch=(ent:Entity,prop:"value"|"code",regex:RegExp):boolean=>{
		const match=ent[prop].match(regex);
		if(match){
			const htmlArr:JSX.Element[]=[];
			match[1] && htmlArr.push((<span>{match[1]}</span>))
			htmlArr.push((<span className="highlight">{match[2]}</span>))
			match[3] && htmlArr.push((<span>{match[3]}</span>))
			htmlArr.push((<span className="type">{ent.type}</span>))
			if(prop=='code')
				htmlArr.push(<span> - {ent.value}</span>)
			ent.html=htmlArr
			return true;
		}
		return false;
	}


	render(){
		return (
			<CompletionInput id={settings.inputId} debounce={settings.debounce} onInput={this.getCompletions.bind(this)} />
		)
	}
}
