import React from 'react'
import {Tile, Props as TileProps} from './tile.component'


type TileContent=TileProps["content"];

//we want to allow any async content to be replaced when the data is available, so
//we expand the type, then in the constructor we 
type AsyncTileProps=Omit<TileProps,'content'> & {content:TileContent|Promise<TileContent>}
type AsyncTiles=Record<string,AsyncTileProps>

export type Props={
	tiles:AsyncTiles
	whileLoading?:TileContent
}

// export class TileGrid<T extends Props> extends React.Component<T,State<T>> {
export class TileGrid extends React.Component<Props> {

	// state:Record<Extract<keyof Props["tiles"],string>,TileContent>
	//To allow all the content of each tile to change after it's been created
	//we'll store that stuff in state. keys will be the keys from Props[tiles]
	//but since we want additional 
	state:Record<string,TileContent>

	private _ID:number;
	log(...args:any[]){
		console.log(this._ID,`<${this.constructor.name}>`,...args);
	}
	warn(...args:any[]){
		console.warn(this._ID,`<${this.constructor.name}>`,...args);
	}

	constructor(props:Props){
		super(props);
		this._ID=Math.floor(Math.random()*10000);
		const state:any={};
		for(const key in this.props.tiles){
			const tile=this.props.tiles[key]
			if(tile.content instanceof Promise){
				tile.content.then(c=>{this.updateTileContent(key,c)});
				state[key]=props.whileLoading||(<span className='loading'></span>)
			}else{
				state[key]=tile.content;
			}
		}
		this.log("creating:",Object.values(this.props.tiles).map(t=>(`${t.title}${t.content instanceof Promise ?'*':''}`)).join(', '));
		this.state=state
	}

	updateTileContent(key:string,content:TileContent){
		if(this.state.hasOwnProperty(key)){
			this.log("updating content of tile",key) 
			this.setState(Object.fromEntries([[key,content]]));
		}else{
			this.warn("no such tile:",key)
		}
	}



  	render() {
  		this.log("rendering:",Object.values(this.props.tiles).map(t=>(`${t.title}${t.content instanceof Promise ?'*':''}`)).join(', '));
  		// console.log(this.props.tiles, this.state);
  		return (
			<div className="tileGrid">{
				Object.entries(this.props.tiles).map(([key,tile])=>
					<Tile key={key} title={tile?.title||key} className={tile?.className} content={this.state[key]} />
				)
			}</div>  
  		)
  	}

}

export default TileGrid
