import React from 'react'
import "./tiles.css";

export type Renderable=JSX.Element|JSX.Element[]|string

export interface Props {
	title:string
	content:Renderable|Promise<Renderable>
	,onClick?:()=>void
	,whileLoading?:Renderable
	,className?:string
}
export interface State {
	marked:boolean
	,content:Renderable
}

export class Tile extends React.Component<Props,State>{
	private _ID:number;
	log(...args:any[]){
		console.log(this._ID,`<${this.constructor.name}>`,...args);
	}
	constructor(props:Props){
		super(props);
		this._ID=Math.floor(Math.random()*10000);
		const c=this.props.content
		this.state={
			marked:false
			,content:props.content instanceof Promise ? props.whileLoading||'' : props.content
		}

		if(props.content instanceof Promise)
			props.content.then(content=>this.setState({content}));

		this.log("constructor:",props.title);
	}
	toggleMarked(){
		this.setState({marked:!this.state.marked})
	}
	render(){
		const cls=['tile'];
		if(this.props.className) cls.push(this.props.className)
		return (
			<div 
				className={cls.join(' ')}
				onClick={this.props.onClick?.bind(this)||console.log}
			>
				<div className="title">{this.props.title}</div>
				<div className="content">
					{this.state.content}
				</div>
			</div>
		);
	}
}

export default Tile
