import React from 'react'

type CompletionList=Array<{name:string,key?:string}>
export type GetCompletions<T extends CompletionList>=(search:string,lastCompletions:T|null)=>T|null

export interface Props<T extends CompletionList> {
	id?:string,
	debounce?:number,
	getCompletions:GetCompletions<T>
}

export class Input<T extends CompletionList> extends React.Component<Props<T>> {


	public lastSearch='';
	public completions:T|null=null;


	get inputHandler(){
		if(this.props?.debounce && this.props.debounce>0)
			return Input.debounceHandler(this.#findCompletions,this.props.debounce,this);
		else
			return this.#findCompletions.bind(this);
	}

	#findCompletions(event:React.ChangeEvent<HTMLInputElement>){
		const lastSearch=this.lastSearch;
		const search=this.lastSearch=event.target.value;
		if(!search){
			this.completions=null;
		}else if(this.props.getCompletions){
			let lastCompletions=null;
			if(lastSearch.startsWith(search)){
				lastCompletions=this.completions;
			}else{
				this.completions=null; //user has pressed backspace => delete the old completions
			}
		 	this.completions=this.props.getCompletions(search,lastCompletions);
		}
	}


	/* eslint-disable-next-line @typescript-eslint/ban-types -- we explicitly expect any function */
	static debounceHandler<F extends (...args:any[])=>any>(fn:F,delay=50,self:any=window) {
		var timer:ReturnType<typeof setInterval>; /* eslint-disable-line no-var -- explicitly refers to entire function */
		return (...args:Parameters<F>)=>{
			clearTimeout(timer);
			timer=setTimeout(()=>fn.apply(self,args),delay);
		}
	}

	render(){		
		var completionList /* eslint-disable-line no-var -- explicitly refers to entire function */
		if(this.completions){
			const list=this.completions.map(
				(c)=>(<div key={c.key||c.name} className='completion'>{c.name}</div>))
			completionList=(<div className="completionList">{list}</div>)
		}
		return (
			<div id={this.props.id}>
				<input type="text" placeholder="Search..." onInput={this.inputHandler} autoFocus></input>
				{completionList}
			</div>
		);
	}


}


export default Input;
