import { GraphQLClient } from 'graphql-request'

// The ./types.ts file is generated by the module graphql-codegen (which has to be run manually) 
// from all .graphql files in this project. We export it so others don't have to import 2 things
export * from './types'

export const uri:string = "https://countries.trevorblades.com";

export const graphql = new GraphQLClient(uri, { headers: {} });

export enum EntityType {
	'Country'
	,'Continent'
	,'Language'
}
const entityTypes=Object.values(EntityType);

export function assertEntityType(x:any):EntityType{
	if(typeof x=='string' && entityTypes.includes(x))
		return x as unknown as EntityType
	else
		throw new TypeError("Not an entity type: "+String(x));
}
