import { GraphQLClient } from 'graphql-request'
import { ListNamesDocument, ListNamesQuery, ListNamesQueryVariables} from 'types/countries';

class GraphQL {
	private readonly client:GraphQLClient;

	constructor(uri:string){
		this.client=new GraphQLClient(uri, { headers: {} })
	}
	listNames(){
		return this.client.request<ListNamesQuery,ListNamesQueryVariables>(ListNamesDocument)
	}
}

if(typeof process.env.REACT_APP_GRAPHQL_URI !='string')
  throw new Error("REACT_APP_GRAPHQL_URI not specified in .env")

export default new GraphQL(process.env.REACT_APP_GRAPHQL_URI);
