import React from 'react'

type CompletionList=Array<{name:string,key?:string,html?:JSX.Element}>
export type GetCompletions<T extends CompletionList>=(search:string,lastCompletions:T|null)=>T|null

export interface Props<T extends CompletionList> {
	id?:string,
	debounce?:number,
	getCompletions:GetCompletions<T>
}

interface State<T extends CompletionList>{
	completions:T|null
}

export class Input<T extends CompletionList> extends React.Component<Props<T>,State<T>> {


	public lastSearch='';

	constructor(props:Props<T>){
		super(props);
		this.state={completions:null};
	}

	get inputHandler(){
		if(this.props?.debounce && this.props.debounce>0)
			return Input.debounceHandler(this.#findCompletions,this.props.debounce,this);
		else
			return this.#findCompletions.bind(this);
	}

	#findCompletions(event:React.ChangeEvent<HTMLInputElement>){
		console.log("updating this.state.completions",this)
		const lastSearch=this.lastSearch;
		const search=this.lastSearch=event.target.value;
		if(!search){
			this.#setCompletionState(null);
		}else if(this.props.getCompletions){
			let lastCompletions=null;
			if(lastSearch.startsWith(search)){
				lastCompletions=this.state.completions;
			}else{
				this.#setCompletionState(null); //user has pressed backspace => delete the old completions
			}
			this.#setCompletionState(this.props.getCompletions(search,lastCompletions));
		}
	}

	#setCompletionState(completions:State<T>["completions"]){
		this.setState({completions});
	}

	#getCompletionListElement(completions:CompletionList):JSX.Element{
		const list=completions.map(
			(c)=>(<div key={c.key||c.name} className='completion'>{c.html||c.name}</div>)
		)
		return (<div className="completionList">{list}</div>)
	}


	/* eslint-disable-next-line @typescript-eslint/ban-types -- we explicitly expect any function */
	static debounceHandler<F extends (...args:any[])=>any>(fn:F,delay=50,self:any=window) {
		var timer:ReturnType<typeof setInterval>; /* eslint-disable-line no-var -- explicitly refers to entire function */
		return (...args:Parameters<F>)=>{
			console.log('debounce',...args);
			clearTimeout(timer);
			timer=setTimeout(()=>{
				console.log('trigger',...args);
				try{fn.apply(self,args)}catch(e){console.error(e)}
			},delay);
		}
	}

	render(){	
		console.log("rendering...")	
		const completionList=(this.state.completions ? this.#getCompletionListElement(this.state.completions) : null);
		return (
			<div id={this.props.id}>
				<input type="text" placeholder="Search..." onInput={this.inputHandler} autoFocus></input>
				{completionList}
			</div>
		);
	}


}


export default Input;
