import React from 'react'
import {TileGrid, Props as GridProps} from 'components/tiles.d/tile-grid.component'
import {Entity, NullableEntityArray} from './country-search.component'

interface Props {
	results:NullableEntityArray
}
interface State {
	status:"waiting"|"loading"|"finished"
}

function randomDelay(min:number,max:number,resolveWith?:any){
	return new Promise(resolve=>{
		const delay=Math.random() * (max - min) + min;
		setTimeout(()=>{
			console.log('resolving with',resolveWith)
			resolve(resolveWith);
		},delay);
	})
}

export default class CountryResults extends React.Component<Props,State> {


	constructor(props:Props){
		super(props);
		this.state={
			status:"waiting"
		};
	}

	setStatus(status:State["status"]){
		this.setState({status});
	}

	componentDidUpdate(): void {
	   if(this.state.status!='finished' && this.props.results){
			console.log('changing state to finished...')
			this.setStatus('finished');
		}else if(!this.props.results && this.state.status=='finished'){
			this.setStatus('waiting');
		}
	}

	render(){
		switch(this.state.status){
		case 'waiting':
			console.log('waiting... rendering nothing')
			return;
		case 'loading':
			console.log('loading... rendering')
			return (<div className='loading'></div>)
		case 'finished':
			console.log('rendering',this.props.results);
			return (
				<div id="output">
					{(!this.props.results||!this.props.results.length)
						? <div className='emptyResults'>No results</div>
						: <TileGrid tiles={this.#buildTileContents(this.props.results)}/>
					}
				</div>
			)
		}		

	}

	#buildTileContents=(results:NonNullable<Props["results"]>)=>{
		const tileContents={};
		for(const ent of results){
			const tile:any={title:ent.value, className:'result-'+ent.type.toLowerCase()};
			switch(ent.type){
			case 'Country':
				tile.content=this.#generateCountryHtml(ent);
				break;
			case 'Continent':
				tile.content=this.#generateContinentHtml(ent);
				break;
			case 'Language':
				tile.content=this.#generateLanguageHtml(ent);
				break;
			}
			tileContents[ent.key]=tile
		}
		return tileContents;
	}


	#generateCountryHtml=(ent:Entity)=>{
		return randomDelay(1000,2000,(<span>{ent.value}</span>));
	}
	#generateContinentHtml=(ent:Entity)=>{
		return randomDelay(1000,2000,(<span>{ent.value}</span>));
	}
	#generateLanguageHtml=(ent:Entity)=>{
		return randomDelay(1000,2000,(<span>{ent.value}</span>));
	}
}
