import React from 'react'
import {TileGrid, Props as GridProps} from 'components/tiles.d/tile-grid.component'
import {Entity, NullableEntityArray} from './country-search.component'

interface Props {
	results:NullableEntityArray
}


function randomDelay<T>(min:number,max:number,resolveWith:T):Promise<T>{
	return new Promise(resolve=>{
		const delay=Math.random() * (max - min) + min;
		setTimeout(()=>{
			console.log('resolving with',resolveWith)
			if(resolveWith==undefined)
				resolve(resolveWith);
		},delay);
	})
}

export default class CountryResults extends React.Component<Props> {

	private currentTiles:GridProps['tiles']={};
	private allTiles:GridProps['tiles']={};

	componentDidUpdate(prevProps: Readonly<Props>): void {
		this.currentTiles={};
		if(this.props.results){
		    for(const ent of this.props.results){
		    	if(!this.allTiles.hasOwnProperty(ent.key)){
					const tile:any={title:ent.value, className:'result-'+ent.type.toLowerCase()};
					switch(ent.type){
					case 'Country':
						tile.content=this.#generateCountryHtml(ent);
						break;
					case 'Continent':
						tile.content=this.#generateContinentHtml(ent);
						break;
					case 'Language':
						tile.content=this.#generateLanguageHtml(ent);
						break;
					}
					tile.content.then((content:JSX.Element)=>tile.content=content)
					this.allTiles[ent.key]=tile
		    	}
		    	this.currentTiles[ent.key]=this.allTiles[ent.key];
			}
		}
		console.log(this.props.results, this.currentTiles)
	}

	#generateCountryHtml=(ent:Entity):Promise<JSX.Element>=>{
		return randomDelay(1000,6000,(<span>{ent.value}</span>));
	}
	#generateContinentHtml=(ent:Entity):Promise<JSX.Element>=>{
		return randomDelay(1000,6000,(<span>{ent.value}</span>));
	}
	#generateLanguageHtml=(ent:Entity):Promise<JSX.Element>=>{
		return randomDelay(1000,6000,(<span>{ent.value}</span>));
	}

	render(){
		return (<div id="output">{
			this.props.results ?
				this.props.results.length ?
					<TileGrid tiles={this.currentTiles}/>
					:<div className='emptyResults'>No results</div>
				:''
		}</div>)
	}





}
