import {
	graphql,
	EntityType,
	assertEntityType,
	Scalars,
	NamesAndCodesQuery,
	NamesAndCodesQueryVariables,
	NamesAndCodesDocument
} from 'services/countries-api/countries-api.service';


export interface Entity{
		name:Scalars['String']
		,search:Lowercase<Scalars['String']>
		,code:Scalars['ID']
		,type:EntityType
		,id:Scalars['String']
}


function makeEntityArray(data:NamesAndCodesQuery):Array<Entity> {
	const list:ReturnType<typeof makeEntityArray>=[];
	Object.entries(data).forEach(([t,arr])=>{
		if(Array.isArray(arr)){
			const type=assertEntityType(t)
			arr.forEach(({code,name})=>{
				name??=code;
				list.push({
					name:String(name), 
					search:name.toLowerCase().replaceAll(/[^a-z]/,''),
					code, 
					type,
					id:`{code}-{type}`
				});
			})
		}
	})
	return list
}

export async function getNamesAndCodes(){
	return graphql.request<NamesAndCodesQuery,NamesAndCodesQueryVariables>(NamesAndCodesDocument).then(makeEntityArray)
}

export default getNamesAndCodes
