import React from 'react'
import {Input} from 'components/input.component'
import {getNamesAndCodes,Entity} from './names-and-codes.query'

type NullableEntityArray=Entity[]|null;

const settings={
	debounce:500
	,inputId:"search"
}

export default class CountryInput extends React.Component {

	private entities:NullableEntityArray=null;

	constructor(props:never){
		super(props)
		getNamesAndCodes().then(ent=>{if(ent.length){this.entities=ent}});
	}

	getCompletions(search:string,entities:NullableEntityArray):NullableEntityArray {
		if(!entities || !entities.length)
			entities=this.entities; //if no subset of entities are passed in then use the entire list

		if(!entities || !entities.length)
			return null;

		const regex=new RegExp(search.length<3 ? '^' : ''+search,'i'); 
		//short strings we only match begining, always case insensistive
		if(search.length<3)
			entities=entities.filter(ent=>ent.name.match(regex)||ent.code.match(regex));
		else
			entities=entities.filter(ent=>ent.name.match(regex));

		return (entities.length ? entities : null);
	}

	render(){
		return (
			<Input id={settings.inputId} debounce={settings.debounce} getCompletions={this.getCompletions.bind(this)} />
		)
	}
}
