import React from 'react'
import {TileGrid, Props as GridProps} from 'components/tiles.d/tile-grid.component'
import {Entity, NullableEntityArray} from './country-search.component'

interface Props {
	results:NullableEntityArray
}

interface State {
	tiles:GridProps['tiles']
}

type Tile=GridProps['tiles'][keyof GridProps['tiles']];

function randomDelay<T>(resolveWith:T):Promise<T>{
	return new Promise(resolve=>{
		const delay=Math.random() * (3000 - 1000) + 1000;
		setTimeout(()=>{
			// console.log('resolving with',resolveWith)
			resolve(resolveWith);
		},delay);
	})
}

export default class CountryResults extends React.Component<Props,State> {
	private _ID:number;
	log(...args:any[]){
		console.log(this._ID,`<${this.constructor.name}>`,...args);
	}

	// private cache:GridProps['tiles']={};

	constructor(props:Props){
		super(props);
		this._ID=Math.floor(Math.random()*10000);
		this.state={tiles:{}};
		this.log("creating",this.props.results?.map(ent=>ent.value).join(', '));
	}


	private getTiles(){
		const tiles:State['tiles']={};
		if(this.props.results && this.props.results.length){
		    for(const ent of this.props.results){
		    	tiles[ent.key]=this.getTile(ent);
			}
		}
		return tiles;
	}
	private getTile=(ent:Entity):Tile=>{
		if(this.state.tiles.hasOwnProperty(ent.key)==false)
			this.setTile(ent.key,this.buildTile(ent))
		return this.state.tiles[ent.key];
	}
	private setTile=(key:keyof State['tiles'],tile:Tile):void=>{
		const tiles={...this.state.tiles};
		tiles[key]=tile;
		this.setState({tiles});
	}
	private buildTile=(ent:Entity):Tile=>{
		const tile:any={title:ent.value, className:ent.type.toLowerCase()};
		tile.content=this[`build${ent.type}TileContent`](ent);
		tile.content.then((content:JSX.Element)=>{tile.content=content})
		return tile;
	}
	private buildCountryTileContent=(ent:Entity):Promise<JSX.Element>=>{
		return randomDelay(<span>{ent.type}</span>);
	}
	private buildContinentTileContent=(ent:Entity):Promise<JSX.Element>=>{
		return randomDelay(<span>{ent.type}</span>);
	}
	private buildLanguageTileContent=(ent:Entity):Promise<JSX.Element>=>{
		return randomDelay(<span>{ent.type}</span>);
	}

	render(){
		const tiles=this.getTiles()
		this.log("rendering:",Object.values(tiles).map(t=>t.title).join(', '));
		return (<div className={this.constructor.name}>{
			this.props.results ?
				this.props.results.length ?
					<TileGrid tiles={tiles}/>
					:<div className='emptyResults'>No results</div>
				:''
		}</div>)
	}





}
