import React from 'react'
import "./tiles.css";

export interface Props {
	content:JSX.Element|JSX.Element[]
	,key?:string
}
export interface State {
	marked:boolean
}

export class Tile extends React.Component<Props,State>{

	constructor(...props:Props){
		super(props);
		this.state={
			marked:false
		}
	}
	toggleMarked(){
		this.setState({marked:!this.state.marked})
	}
	render(){
		return (
			<div 
				className={(this.state.marked?'marked ':'')+'tile'}
				onClick={this.toggleMarked}
			>
				{JSON.stringify(this.props.content)}
			</div>
		);
	}
}

export default Tile
