import React from 'react'
import {Tile, Props as TileProps} from './tile.component'


type AlsoAsync<T>=T|Promise<T>
type Content=TileProps["content"];
type AsyncTileProps=Omit<TileProps,'content'> & {content:Content|Promise<Content>}
type AsyncTiles=Record<string,AsyncTileProps>

export interface Props{
	tiles:AsyncTiles
	whileLoading?:Content
}

// type State<T = AsyncTiles>={
// 	// [K in keyof T]:T[K]['content'] extends Promise<Content> ? Content : never
// 	[K in keyof T]:Record<string,TileProps>
// }

// export class TileGrid<T=AsyncTiles> extends React.Component<Props<T>,State<T>> {
export class TileGrid extends React.Component<Props> {

	constructor(props:Props){
		super(props);
		this.state=props.tiles;
		const state:any={};
		for(const key in this.props.tiles){
			key
			const t:AsyncTileProps=this.props.tiles[key];
			if(t.content instanceof Promise){
				t.content.then(c=>{this.updateTileContent(key,c)});
				t.content=props.whileLoading||(<span className='loading'></span>)
				state[key]=t;
			}
		}
		this.state=state as unknown as State<T>;
		if(!this.props.tiles.hasOwnProperty('balls')){
			this.state.balls
		}
	}
	updateTileContent(key:keyof T,content:Content){
		const tile=this.state[key];
		tile.content=content
		const state={};
		this.setState()
	}
  	render() {return (
		<div className="tileGrid">{
			Object.entries(this.props.tiles).map(([key,{title,content,className}])=>
				<Tile key={key} title={title} className={className} content={content as Content} />
			)
		}</div>  
  	)}

}

export default TileGrid
