import React from 'react'
import {Input,getCompletionSignature} from 'components/input.component'
import {getNamesAndCodes,Entity} from './names-and-codes.query'
import assert from 'services/assert.helper'



export default class CountryInput extends React.Component {

	private entities:Array<Entity>|null=null;

	constructor(){
		super(arguments)
		getNamesAndCodes().then(ent=>this.entities=ent);
	}

	getCompletions(search:string,lastResult?:Array<Entity>):Array<Entity>{
		assert.type(search,'string');
		search=search.toLowerCase();
		const completions:Array<Entity>=[];
		if(lastResult && lastResult.length){
			assert.typedArray(lastResult,Entity)
			completions.push(...lastResult.filter(ent=>ent.name.match(search)));
		}else if(this.entities){
			if(search.length<3){
				completions.push(...this.entities
					.filter(ent=>ent.name.toLowerCase().startsWith(search) || ent.code.toLowerCase().startsWith(search))
				)
			}else{
				completions.push(...this.entities.filter(ent=>ent.name.match(search)));
			}
		}
		return completions;
	}

	render(){
		return (
			<Input id="search" debounce={500} getCompletions={this.getCompletions.bind(this) as getCompletionSignature} />
		)
	}
}
